    <script>
      $(function(){
        $(".gallery-item > a").colorbox({
          photo: true,
          scalePhotos: true,
          transition: "none",
          maxWidth: "90%",
          maxHeight: "90%",
          onOpen: function(){
            $("body").addClass("locked");
          },
          onClosed: function(){
            $("body").removeClass("locked");
          }
        });

        $(window).resize(function(){
          $.colorbox.resize();
        });

        window.onorientationchange = function(){
          $.colorbox.resize();
        };
      });
    </script>
